import React, { useState } from 'react';
import {BsPlusCircle} from 'react-icons/bs'
const Schedule = ()=>{
    const [slots,setslots] = useState([])
    const [days,setdays] = useState([])
    const addschedule=()=>{
        console.log('clicked')
    }

    const daybtn = (e)=>{
        console.log(e.target.checked)
        var checkboxes = document.getElementsByClassName('btnlike');
        for(var i=0, n=checkboxes.length;i<n;i++) {
            if(checkboxes[i].checked){
                console.log('counted')
            }
          }
    }
    return (
        <>
            <div class="modal fade" id="exampleModalLong" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLongTitle" aria-hidden="true">
            <div class="modal-dialog" role="document">
                <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="exampleModalLongTitle">Add Work Time</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div class="modal-body">
                    <strong className='float-left' >Days Active </strong>
                    <div className='mt-5'>
                        <label><input onChange={daybtn} className="btnlike monday" name='monday' type="checkbox"/><span>M</span></label>
                        <label><input onChange={daybtn} className="btnlike tuesday" name='tuesday' type="checkbox"/><span>T</span></label>
                        <label><input onChange={daybtn} className="btnlike wednessday" name='wednessday' type="checkbox"/><span>W</span></label>
                        <label><input onChange={daybtn} className="btnlike thursday" name='thursday' type="checkbox"/><span>T</span></label>
                        <label><input onChange={daybtn} className="btnlike friday" name='friday' type="checkbox"/><span>F</span></label>
                        <label><input onChange={daybtn} className="btnlike saturday" name='saturday' type="checkbox"/><span>S</span></label>
                        <label><input onChange={daybtn} className="btnlike sunday" name='sunday' type="checkbox"/><span>S</span></label>

                        {/* <button onClick={()=>daybtn('tuesday')} className='days btn-light btn m-1 tuesday' id='tuesday'>T</button>
                        <button onClick={()=>daybtn('wednessday')} className='days btn-light btn m-1 wednessday' id='wednessday'>W</button>
                        <button onClick={()=>daybtn('thursday')} className='days btn-light btn m-1 thursday' id='thursday'>T</button>
                        <button onClick={()=>daybtn('friday')} className='days btn-light btn m-1 friday' id='friday'>F</button>
                        <button onClick={()=>daybtn('saturday')} className='days btn-light btn m-1 saturday' id='saturday'>S</button>
                        <button onClick={()=>daybtn('sunday')} className='days btn-light btn m-1 sunday' id='sunday'>S</button> */}
                    </div>
                    
                    {/* enter all the slots here */}
                    
                    <div className='slots mt-3'>
                        {
                            slots.map((item)=>{
                                return(
                                    <div>
                                        <p>{item}</p>
                                    </div>
                                )
                            })
                        }
                    </div>
                    <div className='row'>
                        <form className='mt-4 col-6'>
                            <label for="appt">From</label><br/>
                            <input className='ml-2' type="time" id="appt" name="appt"/>
                        </form>
                        <form className='mt-4 col-6'>
                            <label for="appt">To</label><br/>
                            <input className='ml-2' type="time" id="appt" name="appt"/>
                        </form>
                        
                        <p className='m-auto text-success addslot' >Add Slot</p>
                    </div>

                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary closemodal" data-dismiss="modal">Close</button>
                    <button type="button"  class="btn btn-primary">Save changes</button>
                </div>
                </div>
            </div>
            </div>

            <div>
            <h2>Schedules</h2>

            {/* map array of schedules here */}
        
            <h1 data-toggle="modal" data-target="#exampleModalLong"><BsPlusCircle onClick={addschedule} className='addSchedule'/></h1>
            <strong>Add your work timings</strong>
            </div>
        </>
    )
}

export default Schedule;
